import { exec } from "child_process";
export class Git {
    async isGitRepository(directory) {
        return await new Promise((resolve) => {
            exec("git rev-parse --is-inside-work-tree", { cwd: directory }, (error) => {
                resolve(!error);
            });
        });
    }
    async hasUnsavedChanges(directory) {
        const hasUnsavedChanges = await new Promise((resolve) => {
            exec("git status --porcelain", { cwd: directory }, (error, stdout) => {
                resolve(!!error || !!stdout);
            });
        });
        return hasUnsavedChanges;
    }
}
